package Benchmark.Generator.Targets;

import Benchmark.Generator.GeneratedData.IGeneratedEntry;

/**
 * Facilitates transparently keeping track of the number of entries generated by the data-generator.
 */
public class CountTarget implements ITarget {
    private long count = 0;

    public long getCount(){
        return count;
    }

    @Override
    public void add(IGeneratedEntry entry) {
        count++;
    }

    @Override
    public boolean shouldStopEarly() {
        return false;
    }

    @Override
    public void close() { }
}
